                <Button 
                  className="w-full mt-6" 
                  size="lg"
                  onClick={() => {
                    const element = document.querySelector('[data-testid="button-whatsapp-toggle"]') as HTMLElement;
                    if (element) element.click();
                  }}
                  data-testid="button-book-now"
                >
                  Reservar Agora
                </Button>
              </div>
            </motion.div>
          </div>
        </div>
      </div>

      <Footer />
      <WhatsAppButton />
      <AIChatbot />
    </div>
  );
}

