import { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { MessageCircle, X, Phone } from 'lucide-react';
import { Button } from '@/components/ui/button';

const whatsappNumbers = [
  { number: '+55 31 99085-8868', label: 'Atendimento 1' },
  { number: '+55 31 99932-5441', label: 'Atendimento 2' },
  { number: '+55 31 99808-3540', label: 'Atendimento 3' },
];

export default function WhatsAppButton() {
  const [isOpen, setIsOpen] = useState(false);

  const handleWhatsApp = (number: string) => {
    const cleanNumber = number.replace(/\D/g, '');
    window.open(`https://api.whatsapp.com/send/?phone=${cleanNumber}&text&type=phone_number`, '_blank');
  };

  return (
    <>
      <AnimatePresence>
        {isOpen && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            className="fixed inset-0 bg-black/50 z-40"
            onClick={() => setIsOpen(false)}
            data-testid="overlay-whatsapp"
          />
        )}
      </AnimatePresence>

      <AnimatePresence>
        {isOpen && (
          <motion.div
            initial={{ opacity: 0, y: 100, scale: 0.8 }}
            animate={{ opacity: 1, y: 0, scale: 1 }}
            exit={{ opacity: 0, y: 100, scale: 0.8 }}
            className="fixed bottom-24 right-4 md:right-8 bg-card border border-card-border rounded-md p-4 shadow-xl z-50 w-[calc(100vw-2rem)] md:w-80"
            data-testid="popup-whatsapp"
          >
            <div className="flex items-center justify-between mb-4">
              <h3 className="font-semibold" data-testid="text-whatsapp-title">Fale Conosco pelo WhatsApp</h3>
              <Button 
                size="icon" 
                variant="ghost"
                onClick={() => setIsOpen(false)}
                data-testid="button-close-whatsapp"
              >
