shared/schema.ts.content:
import { sql } from "drizzle-orm";
import { pgTable, text, varchar, integer, timestamp } from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

export const users = pgTable("users", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  username: text("username").notNull().unique(),
  password: text("password").notNull(),
});

export const feedback = pgTable("feedback", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  name: text("name").notNull(),
  email: text("email").notNull(),
  rating: integer("rating").notNull(),
  message: text("message").notNull(),
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

export const insertUserSchema = createInsertSchema(users).pick({
  username: true,
  password: true,
});

export const insertFeedbackSchema = createInsertSchema(feedback).omit({
  id: true,
  createdAt: true,
});

export type InsertUser = z.infer<typeof insertUserSchema>;
export type User = typeof users.$inferSelect;
export type InsertFeedback = z.infer<typeof insertFeedbackSchema>;
export type Feedback = typeof feedback.$inferSelect;

client/src/pages/ChatPage.tsx.content:
import { useState, useRef, useEffect } from 'react';
import { motion } from 'framer-motion';
import { ArrowLeft, Send, Sparkles } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Avatar, AvatarImage, AvatarFallback } from '@/components/ui/avatar';
import { useLocation } from 'wouter';
import barbaraAvatar from '@assets/barbara-avatar.png';

interface Message {
  role: 'user' | 'assistant';
  content: string;
}

export default function ChatPage() {
  const [, navigate] = useLocation();
  const [messages, setMessages] = useState<Message[]>([
    {
      role: 'assistant',
      content: 'Olá! Sou a Barbara, sua consultora de viagens da Roda Bem Turismo! 😊 Procurando por uma aventura incrível? Como posso te ajudar?'
    }
  ]);
  const [input, setInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const messagesEndRef = useRef<HTMLDivElement>(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSend = async () => {
    if (!input.trim() || isLoading) return;

    const userMessage = input.trim();
    setInput('');
    setMessages(prev => [...prev, { role: 'user', content: userMessage }]);
    setIsLoading(true);

    try {
      const response = await fetch('/api/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          message: userMessage,
          history: messages.map(m => ({ role: m.role, content: m.content }))
        })
      });

      const data = await response.json();

      if (data.message) {
        setMessages(prev => [...prev, { role: 'assistant', content: data.message }]);
      } else {
        throw new Error(data.error || 'Erro desconhecido');
      }
    } catch (error) {
      setMessages(prev => [...prev, { 
        role: 'assistant', 
        content: 'Desculpe, ocorreu um erro. Por favor, tente novamente ou entre em contato pelo WhatsApp: (31) 99085-8868' 
      }]);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="flex flex-col h-screen bg-background">
      {/* Header */}
      <div className="flex items-center justify-between p-4 border-b border-border/20 bg-gradient-to-r from-primary/10 to-accent/10">
        <div className="flex items-center gap-3">
          <Button
            size="icon"
            variant="ghost"
            className="rounded-full"
            onClick={() => navigate('/')}
            data-testid="button-back-chat"
          >
            <ArrowLeft className="w-5 h-5" />
          </Button>
          <div className="relative">
            <Avatar className="w-12 h-12 border-2 border-primary/30">
              <AvatarImage src={barbaraAvatar} alt="Barbara" />
              <AvatarFallback>BA</AvatarFallback>
            </Avatar>
            <div className="absolute -bottom-1 -right-1 w-4 h-4 bg-primary rounded-full border-2 border-card" />
          </div>
          <div>
            <h3 className="font-bold text-lg" data-testid="text-chatbot-title">Barbara</h3>
            <p className="text-xs text-muted-foreground flex items-center gap-1">
              <span className="w-2 h-2 bg-primary rounded-full animate-pulse" />
              Online agora
            </p>
          </div>
        </div>
      </div>

      {/* Messages */}
      <div className="flex-1 overflow-y-auto p-4 space-y-4 bg-background/50">
        {messages.map((msg, idx) => (
          <motion.div
            key={idx}
            initial={{ opacity: 0, y: 10 }}
            animate={{ opacity: 1, y: 0 }}
            className={`flex ${msg.role === 'user' ? 'justify-end' : 'justify-start'}`}
            data-testid={`message-${msg.role}-${idx}`}
          >
            {msg.role === 'assistant' && (
              <Avatar className="w-8 h-8 mr-2 flex-shrink-0">
                <AvatarImage src={barbaraAvatar} alt="Barbara" />
                <AvatarFallback>BA</AvatarFallback>
              </Avatar>
            )}
            <div className={`max-w-[75%] rounded-2xl px-4 py-3 ${
              msg.role === 'user' 
                ? 'bg-primary text-primary-foreground rounded-br-sm' 
                : 'bg-card border border-border rounded-bl-sm'
            }`}>
              <p className="text-sm leading-relaxed">{msg.content}</p>
            </div>
          </motion.div>
        ))}
        {isLoading && (
          <div className="flex justify-start items-center gap-2">
            <Avatar className="w-8 h-8 flex-shrink-0">
              <AvatarImage src={barbaraAvatar} alt="Barbara" />
              <AvatarFallback>BA</AvatarFallback>
            </Avatar>
            <div className="bg-card border border-border rounded-2xl rounded-bl-sm px-4 py-3">
              <div className="flex gap-1">
                <motion.div
                  animate={{ scale: [1, 1.2, 1] }}
                  transition={{ duration: 0.6, repeat: Infinity, delay: 0 }}
                  className="w-2 h-2 bg-muted-foreground rounded-full"
                />
                <motion.div
                  animate={{ scale: [1, 1.2, 1] }}
                  transition={{ duration: 0.6, repeat: Infinity, delay: 0.2 }}
                  className="w-2 h-2 bg-muted-foreground rounded-full"
                />
                <motion.div
                  animate={{ scale: [1, 1.2, 1] }}
                  transition={{ duration: 0.6, repeat: Infinity, delay: 0.4 }}
                  className="w-2 h-2 bg-muted-foreground rounded-full"
                />
              </div>
            </div>
          </div>
        )}
        <div ref={messagesEndRef} />
      </div>

      {/* Input */}
      <div className="p-4 border-t border-border/20 bg-card/50">
        <form 
          onSubmit={(e) => { e.preventDefault(); handleSend(); }}
          className="flex gap-2"
        >
          <Input
            value={input}
            onChange={(e) => setInput(e.target.value)}
            placeholder="Digite sua mensagem..."
            disabled={isLoading}
            className="rounded-full border-2 px-4"
            data-testid="input-chat-message"
          />
          <Button 
            type="submit" 
            size="icon"
            disabled={isLoading || !input.trim()}
            className="rounded-full w-12 h-12 flex-shrink-0"
            data-testid="button-send-message"
          >
            <Send className="w-5 h-5" />
          </Button>
        </form>
      </div>
    </div>
  );
}

server/firebase.ts.content:
import admin from 'firebase-admin';

interface Destination {
  id: string;
  name: string;
  location: string;
  description: string;
  highlights: string[];
  bestTime: string;
  climate: string;
  price?: number;
  duration?: string;
  includedServices?: string[];
  availableSpots?: number;
}

interface Booking {
  id: string;
  customerName: string;
  destinationId: string;
  packageId?: string;
  status: 'pending' | 'confirmed' | 'cancelled' | 'completed';
  bookingDate: string;
  travelDate: string;
  numberOfPeople: number;
  totalPrice: number;
  paymentStatus?: string;
  specialRequests?: string;
}

interface Customer {
  id: string;
  name: string;
  email: string;
  phone: string;
  preferences?: string[];
  bookingHistory?: string[];
}

interface Package {
  id: string;
  name: string;
  destinationId: string;
  description: string;
  price: number;
  duration: string;
  included: string[];
  maxPeople: number;
  availableSpots?: number;
}

interface Testimonial {
  id: string;
  customerName: string;
  destinationId?: string;
  rating: number;
  comment: string;
  date: string;
  verified?: boolean;
}

interface AllFirebaseData {
  destinations: Destination[];
  bookings: Booking[];
  customers: Customer[];
  packages: Package[];
  testimonials: Testimonial[];
  customData: Record<string, any>;
}

class FirebaseService {
  private db: admin.database.Database | null = null;
  private destinationsCache: Destination[] = [];
  private bookingsCache: Booking[] = [];
  private customersCache: Customer[] = [];
  private packagesCache: Package[] = [];
  private testimonialsCache: Testimonial[] = [];
  private customDataCache: Record<string, any> = {};
  private lastCacheUpdate: number = 0;
  private CACHE_TTL = 5 * 60 * 1000; // 5 minutes

  async initialize() {
    if (admin.apps.length === 0) {
      try {
        const serviceAccount = process.env.FIREBASE_SERVICE_ACCOUNT 
          ? JSON.parse(process.env.FIREBASE_SERVICE_ACCOUNT)
          : null;

        const databaseURL = process.env.FIREBASE_DATABASE_URL;

        if (!serviceAccount || !databaseURL) {
          console.warn('Firebase credentials not configured. Chatbot will work with limited data.');
          return;
        }

        admin.initializeApp({
          credential: admin.credential.cert(serviceAccount),
          databaseURL: databaseURL
        });

        this.db = admin.database();
        console.log('Firebase initialized successfully');

        // Warm up cache on startup - await to ensure data is loaded
        await this.refreshDestinationsCache();
      } catch (error) {
        console.error('Failed to initialize Firebase:', error);
      }
    } else {
      this.db = admin.database();
    }
  }

  async refreshAllCaches(): Promise<void> {
    if (!this.db) return;

    try {
      // Fetch all data nodes in parallel for better performance
      const [destinationsSnap, bookingsSnap, customersSnap, packagesSnap, testimonialsSnap, rootSnap] = await Promise.all([
        this.db.ref('destinations').once('value'),
        this.db.ref('bookings').once('value'),
        this.db.ref('customers').once('value'),
        this.db.ref('packages').once('value'),
        this.db.ref('testimonials').once('value'),
        this.db.ref('/').once('value')
      ]);

      // Process destinations - reset cache first to handle deletions
      const destinationsData = destinationsSnap.val();
      this.destinationsCache = destinationsData 
        ? Object.keys(destinationsData).map(id => ({ id, ...destinationsData[id] }))
        : [];

      // Process bookings - reset cache first to handle deletions
      const bookingsData = bookingsSnap.val();
      this.bookingsCache = bookingsData
        ? Object.keys(bookingsData).map(id => ({ id, ...bookingsData[id] }))
        : [];

      // Process customers - reset cache first to handle deletions
      const customersData = customersSnap.val();
      this.customersCache = customersData
        ? Object.keys(customersData).map(id => ({ id, ...customersData[id] }))
        : [];

      // Process packages - reset cache first to handle deletions
      const packagesData = packagesSnap.val();
      this.packagesCache = packagesData
        ? Object.keys(packagesData).map(id => ({ id, ...packagesData[id] }))
        : [];

      // Process testimonials - reset cache first to handle deletions
      const testimonialsData = testimonialsSnap.val();
      this.testimonialsCache = testimonialsData
        ? Object.keys(testimonialsData).map(id => ({ id, ...testimonialsData[id] }))
        : [];

      // Store any additional custom data from root - reset to handle deletions
      const rootData = rootSnap.val();
      this.customDataCache = rootData ? { ...rootData } : {};

      this.lastCacheUpdate = Date.now();
      console.log(`📊 Firebase data loaded: ${this.destinationsCache.length} destinations, ${this.bookingsCache.length} bookings, ${this.customersCache.length} customers, ${this.packagesCache.length} packages, ${this.testimonialsCache.length} testimonials`);
    } catch (error) {
      console.error('Error fetching data from Firebase:', error);
    }
  }

  async refreshDestinationsCache(): Promise<void> {
    await this.refreshAllCaches();
  }

  async refreshCacheIfNeeded(): Promise<void> {
    if (Date.now() - this.lastCacheUpdate > this.CACHE_TTL) {
      await this.refreshAllCaches();
    }
  }

  async getDestinations(): Promise<Destination[]> {
    await this.refreshCacheIfNeeded();
    return this.destinationsCache;
  }

  async getBookings(): Promise<Booking[]> {
    await this.refreshCacheIfNeeded();
    return this.bookingsCache;
  }

  async getCustomers(): Promise<Customer[]> {
    await this.refreshCacheIfNeeded();
    return this.customersCache;
  }

  async getPackages(): Promise<Package[]> {
    await this.refreshCacheIfNeeded();
    return this.packagesCache;
  }

  async getTestimonials(): Promise<Testimonial[]> {
    await this.refreshCacheIfNeeded();
    return this.testimonialsCache;
  }

  async getAllData(): Promise<AllFirebaseData> {
    await this.refreshCacheIfNeeded();
    return {
      destinations: this.destinationsCache,
      bookings: this.bookingsCache,
      customers: this.customersCache,
      packages: this.packagesCache,
      testimonials: this.testimonialsCache,
      customData: this.customDataCache
    };
  }

  async getDestinationById(id: string): Promise<Destination | null> {
    const destinations = await this.getDestinations();
    return destinations.find(d => d.id === id) || null;
  }

  async getDestinationsSummary(): Promise<string> {
    const destinations = await this.getDestinations();

    if (destinations.length === 0) {
      return 'Nenhum destino disponível no momento.';
    }

    return destinations.map(dest => {
      const parts = [
        `**${dest.name}** (${dest.location})`,
        dest.availableSpots !== undefined ? `Vagas: ${dest.availableSpots}` : null,
        dest.price ? `Preço: R$ ${dest.price}` : null,
        dest.duration ? `Duração: ${dest.duration}` : null,
        dest.bestTime ? `Melhor época: ${dest.bestTime}` : null,
        dest.highlights?.length > 0 ? `Destaques: ${dest.highlights.slice(0, 3).join(', ')}` : null
      ].filter(Boolean);

      return parts.join(' | ');
    }).join('\n\n');
  }

  async getComprehensiveDataSummary(): Promise<string> {
    const allData = await this.getAllData();

    let summary = '';

    // Destinations summary - clear and concise
    if (allData.destinations.length > 0) {
      summary += '=== DESTINOS ===\n';
      summary += allData.destinations.map(dest => {
        let line = `${dest.name}`;
        if (dest.price) line += ` - R$ ${dest.price}`;
        if (dest.availableSpots !== undefined) line += ` - ${dest.availableSpots} vagas`;
        if (dest.duration) line += ` - ${dest.duration}`;
        return line;
      }).join('\n');
      summary += '\n\n';
    }

    // Packages summary
    if (allData.packages.length > 0) {
      summary += '=== PACOTES DISPONÍVEIS ===\n';
      summary += allData.packages.map(pkg => {
        const spots = pkg.availableSpots !== undefined ? pkg.availableSpots : pkg.maxPeople;
        return `${pkg.name} - R$ ${pkg.price} (${pkg.duration}) - ${spots} vagas`;
      }).join('\n');
      summary += '\n\n';
    }

    // Recent bookings stats
    if (allData.bookings.length > 0) {
      const confirmedBookings = allData.bookings.filter(b => b.status === 'confirmed').length;
      const pendingBookings = allData.bookings.filter(b => b.status === 'pending').length;
      summary += `=== ESTATÍSTICAS DE RESERVAS ===\n`;
      summary += `Total de reservas: ${allData.bookings.length}\n`;
      summary += `Confirmadas: ${confirmedBookings}\n`;
      summary += `Pendentes: ${pendingBookings}\n\n`;
    }

    // Testimonials summary
    if (allData.testimonials.length > 0) {
      const avgRating = allData.testimonials.reduce((sum, t) => sum + t.rating, 0) / allData.testimonials.length;
      summary += `=== AVALIAÇÕES ===\n`;
      summary += `Total de avaliações: ${allData.testimonials.length}\n`;
      summary += `Avaliação média: ${avgRating.toFixed(1)}/5\n`;
      const recentTestimonials = allData.testimonials.slice(-3);
      if (recentTestimonials.length > 0) {
        summary += 'Avaliações recentes:\n';
        recentTestimonials.forEach(t => {
          summary += `- ${t.customerName}: ${t.rating}/5 - "${t.comment.substring(0, 100)}${t.comment.length > 100 ? '...' : ''}"\n`;
        });
      }
      summary += '\n';
    }

    // Customer count
    if (allData.customers.length > 0) {
      summary += `=== CLIENTES ===\n`;
      summary += `Total de clientes cadastrados: ${allData.customers.length}\n\n`;
    }

    // Custom data summary
    const relevantCustomKeys = Object.keys(allData.customData).filter(
      key => !['destinations', 'bookings', 'customers', 'packages', 'testimonials'].includes(key)
    );
    if (relevantCustomKeys.length > 0) {
      summary += `=== DADOS ADICIONAIS ===\n`;
      relevantCustomKeys.forEach(key => {
        summary += `${key}: ${JSON.stringify(allData.customData[key]).substring(0, 200)}\n`;
      });
    }

    return summary.trim() || 'Nenhum dado disponível no momento.';
  }
}

export const firebaseService = new FirebaseService();

static-site/styles.css.content:
/* Reset */
*, *::before, *::after {
  box-sizing: border-box;
  margin: 0;
  padding: 0;
}

:root {
  --primary: #1a5f4a;
  --primary-light: #238466;
  --accent: #c4a35a;
  --bg: #fafafa;
  --bg-alt: #f3f3f3;
  --text: #1a1a1a;
  --text-muted: #666;
  --text-light: #888;
  --border: #e5e5e5;
  --white: #fff;
  --shadow: 0 2px 8px rgba(0,0,0,0.06);
  --shadow-lg: 0 8px 30px rgba(0,0,0,0.1);
  --radius: 8px;
  --radius-lg: 16px;
}

html {
  scroll-behavior: smooth;
}

body {
  font-family: 'DM Sans', -apple-system, BlinkMacSystemFont, sans-serif;
  background: var(--bg);
  color: var(--text);
  line-height: 1.6;
  -webkit-font-smoothing: antialiased;
}

.container {
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 20px;
}

/* Header */
.header {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  z-index: 1000;
  padding: 16px 0;
  transition: all 0.3s ease;
}

.header.scrolled {
  background: rgba(255,255,255,0.95);
  backdrop-filter: blur(10px);
  box-shadow: var(--shadow);
  padding: 12px 0;
}

.header-inner {
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 20px;
  display: flex;
  align-items: center;
  justify-content: space-between;
}

.logo {
  font-family: 'DM Serif Display', serif;
  font-size: 1.5rem;
  font-weight: 400;
  color: var(--white);
  text-decoration: none;
  transition: color 0.3s;
}

.header.scrolled .logo {
  color: var(--primary);
}

.nav-desktop {
  display: none;
  gap: 32px;
}

@media (min-width: 768px) {
  .nav-desktop {
    display: flex;
  }
}

.nav-desktop a {
  color: rgba(255,255,255,0.9);
  text-decoration: none;
  font-size: 0.9rem;
  font-weight: 500;
  transition: color 0.2s;
}

.nav-desktop a:hover {
  color: var(--white);
}

.header.scrolled .nav-desktop a {
  color: var(--text-muted);
}

.header.scrolled .nav-desktop a:hover {
  color: var(--primary);
}

.header-cta {
  display: none;
  padding: 10px 20px;
  background: var(--white);
  color: var(--primary);
  border-radius: var(--radius);
  text-decoration: none;
  font-size: 0.875rem;
  font-weight: 600;
  transition: all 0.2s;
}

@media (min-width: 768px) {
  .header-cta {
    display: block;
  }
}

.header-cta:hover {
  transform: translateY(-1px);
  box-shadow: var(--shadow-lg);
}

.header.scrolled .header-cta {
  background: var(--primary);
  color: var(--white);
}

/* Mobile Nav */
.nav-mobile {
  display: flex;
  position: fixed;
  bottom: 0;
  left: 0;
  right: 0;
  z-index: 1000;
  background: var(--white);
  border-top: 1px solid var(--border);
  padding: 8px 0;
  padding-bottom: max(8px, env(safe-area-inset-bottom));
}

@media (min-width: 768px) {
  .nav-mobile {
    display: none;
  }
}

.nav-mobile-link {
  flex: 1;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 4px;
  padding: 8px 4px;
  text-decoration: none;
  color: var(--text-muted);
  font-size: 0.65rem;
  font-weight: 500;
  transition: color 0.2s;
}

.nav-mobile-link.active,
.nav-mobile-link:hover {
  color: var(--primary);
}

/* Buttons */
.btn {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  gap: 8px;
  padding: 14px 28px;
  border-radius: var(--radius);
  font-size: 0.9rem;
  font-weight: 600;
  text-decoration: none;
  transition: all 0.2s ease;
  cursor: pointer;
  border: none;
}

.btn-white {
  background: var(--white);
  color: var(--text);
}

.btn-white:hover {
  transform: translateY(-2px);
  box-shadow: var(--shadow-lg);
}

.btn-outline-white {
  background: transparent;
  color: var(--white);
  border: 2px solid rgba(255,255,255,0.4);
}

.btn-outline-white:hover {
  background: rgba(255,255,255,0.1);
  border-color: rgba(255,255,255,0.6);
}

.btn-primary {
  background: var(--primary);
  color: var(--white);
}

.btn-primary:hover {
  background: var(--primary-light);
  transform: translateY(-2px);
  box-shadow: var(--shadow-lg);
}

.btn-whatsapp {
  background: #25D366;
  color: var(--white);
  padding: 16px 32px;
  font-size: 1rem;
}

.btn-whatsapp:hover {
  background: #1fb855;
  transform: translateY(-2px);
}

/* Hero */
.hero {
  position: relative;
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  overflow: hidden;
}

.hero-bg {
  position: absolute;
  inset: 0;
  background-size: cover;
  background-position: center;
  z-index: -2;
}

.hero-overlay {
  position: absolute;
  inset: 0;
  background: linear-gradient(to bottom, rgba(0,0,0,0.5) 0%, rgba(0,0,0,0.4) 50%, rgba(0,0,0,0.6) 100%);
  z-index: -1;
}

.hero-content {
  text-align: center;
  color: var(--white);
  padding: 100px 20px 80px;
  max-width: 800px;
}

.hero-tag {
  display: inline-block;
  padding: 8px 16px;
  background: rgba(255,255,255,0.15);
  border-radius: 100px;
  font-size: 0.8rem;
  font-weight: 500;
  letter-spacing: 0.5px;
  margin-bottom: 24px;
}

.hero-title {
  font-family: 'DM Serif Display', serif;
  font-size: 2.75rem;
  font-weight: 400;
  line-height: 1.15;
  margin-bottom: 20px;
}

@media (min-width: 768px) {
  .hero-title {
    font-size: 4rem;
  }
}

.hero-desc {
  font-size: 1rem;
  opacity: 0.9;
  max-width: 500px;
  margin: 0 auto 32px;
  line-height: 1.7;
}

@media (min-width: 768px) {
  .hero-desc {
    font-size: 1.1rem;
  }
}

.hero-actions {
  display: flex;
  flex-direction: column;
  gap: 12px;
  align-items: center;
  margin-bottom: 48px;
}

@media (min-width: 480px) {
  .hero-actions {
    flex-direction: row;
    justify-content: center;
  }
}

.hero-stats {
  display: flex;
  justify-content: center;
  gap: 32px;
  flex-wrap: wrap;
}

.stat-item {
  text-align: center;
}

.stat-number {
  display: block;
  font-family: 'DM Serif Display', serif;
  font-size: 2rem;
  font-weight: 400;
}

.stat-label {
  font-size: 0.75rem;
  opacity: 0.7;
  text-transform: uppercase;
  letter-spacing: 0.5px;
}

/* Sections */
.section {
  padding: 80px 0;
}

@media (min-width: 768px) {
  .section {
    padding: 120px 0;
  }
}

.section-header {
  text-align: center;
  max-width: 600px;
  margin: 0 auto 48px;
}

.section-tag {
  display: inline-block;
  color: var(--primary);
  font-size: 0.8rem;
  font-weight: 600;
  text-transform: uppercase;
  letter-spacing: 1px;
  margin-bottom: 12px;
}

.section-title {
  font-family: 'DM Serif Display', serif;
  font-size: 2rem;
  font-weight: 400;
  margin-bottom: 16px;
  color: var(--text);
}

@media (min-width: 768px) {
  .section-title {
    font-size: 2.5rem;
  }
}

.section-desc {
  color: var(--text-muted);
  font-size: 1rem;
  line-height: 1.7;
}

/* Destinations */
.destinations {
  background: var(--bg);
}

.destinations-grid {
  display: grid;
  grid-template-columns: 1fr;
  gap: 16px;
}

@media (min-width: 640px) {
  .destinations-grid {
    grid-template-columns: repeat(2, 1fr);
  }
}

@media (min-width: 1024px) {
  .destinations-grid {
    grid-template-columns: repeat(4, 1fr);
  }
}

.dest-card {
  position: relative;
  border-radius: var(--radius-lg);
  overflow: hidden;
  aspect-ratio: 4/5;
  cursor: pointer;
}

.dest-card img {
  width: 100%;
  height: 100%;
  object-fit: cover;
  transition: transform 0.4s ease;
}

.dest-card:hover img {
  transform: scale(1.05);
}

.dest-overlay {
  position: absolute;
  bottom: 0;
  left: 0;
  right: 0;
  padding: 24px 20px;
  background: linear-gradient(to top, rgba(0,0,0,0.7) 0%, transparent 100%);
  color: var(--white);
}

.dest-tag {
  display: inline-block;
  padding: 4px 10px;
  background: rgba(255,255,255,0.2);
  border-radius: 100px;
  font-size: 0.7rem;
  font-weight: 500;
  margin-bottom: 8px;
}

.dest-overlay h3 {
  font-family: 'DM Serif Display', serif;
  font-size: 1.25rem;
  font-weight: 400;
  margin-bottom: 4px;
}

.dest-overlay p {
  font-size: 0.8rem;
  opacity: 0.8;
}

.destinations-cta {
  text-align: center;
  margin-top: 48px;
}

/* About */
.about {
  background: var(--bg-alt);
}

.about-grid {
  display: grid;
  gap: 48px;
  align-items: center;
}

@media (min-width: 768px) {
  .about-grid {
    grid-template-columns: 1fr 1fr;
    gap: 64px;
  }
}

.about-content .section-tag,
.about-content .section-title {
  text-align: left;
}

.about-text {
  color: var(--text-muted);
  margin-bottom: 32px;
  line-height: 1.8;
}

.features-list {
  display: grid;
  gap: 20px;
}

.feature {
  display: flex;
  gap: 16px;
  align-items: flex-start;
}

.feature-icon {
  flex-shrink: 0;
  width: 48px;
  height: 48px;
  background: var(--primary);
  color: var(--white);
  border-radius: var(--radius);
  display: flex;
  align-items: center;
  justify-content: center;
}

.feature h4 {
  font-size: 1rem;
  font-weight: 600;
  margin-bottom: 4px;
}

.feature p {
  font-size: 0.875rem;
  color: var(--text-muted);
}

.about-image {
  display: none;
}

@media (min-width: 768px) {
  .about-image {
    display: block;
  }
}

.about-image img {
  width: 100%;
  max-width: 400px;
  margin: 0 auto;
  display: block;
  border-radius: var(--radius-lg);
  box-shadow: var(--shadow-lg);
}

/* Testimonials */
.testimonials {
  background: var(--bg);
}

.testimonials-grid {
  display: grid;
  gap: 20px;
}

@media (min-width: 640px) {
  .testimonials-grid {
    grid-template-columns: repeat(2, 1fr);
  }
}

@media (min-width: 1024px) {
  .testimonials-grid {
    grid-template-columns: repeat(3, 1fr);
  }
}

.testimonial-card {
  background: var(--white);
  border: 1px solid var(--border);
  border-radius: var(--radius-lg);
  padding: 24px;
  transition: all 0.2s ease;
}

.testimonial-card:hover {
  transform: translateY(-4px);
  box-shadow: var(--shadow-lg);
}

.testimonial-top {
  display: flex;
  gap: 12px;
  align-items: center;
  margin-bottom: 16px;
}

.testimonial-avatar {
  width: 48px;
  height: 48px;
  background: var(--primary);
  color: var(--white);
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  font-weight: 600;
  font-size: 0.875rem;
}

.testimonial-top h4 {
  font-size: 0.95rem;
  font-weight: 600;
  margin-bottom: 2px;
}

.testimonial-trip {
  font-size: 0.8rem;
  color: var(--primary);
  font-weight: 500;
}

.testimonial-text {
  font-size: 0.9rem;
  color: var(--text-muted);
  line-height: 1.7;
  font-style: italic;
}

/* Contact */
.contact {
  background: var(--primary);
  color: var(--white);
}

.contact-box {
  display: grid;
  gap: 48px;
  align-items: center;
}

@media (min-width: 768px) {
  .contact-box {
    grid-template-columns: 1fr auto;
    gap: 64px;
  }
}

.contact-content h2 {
  font-family: 'DM Serif Display', serif;
  font-size: 2rem;
  font-weight: 400;
  margin-bottom: 16px;
}

@media (min-width: 768px) {
  .contact-content h2 {
    font-size: 2.5rem;
  }
}

.contact-content p {
  opacity: 0.9;
  margin-bottom: 24px;
  max-width: 500px;
}

.contact-info {
  display: flex;
  flex-direction: column;
  gap: 16px;
}

.info-item {
  display: flex;
  gap: 12px;
  align-items: center;
}

.info-item svg {
  opacity: 0.8;
}

.info-item strong {
  display: block;
  font-size: 0.8rem;
  opacity: 0.7;
  font-weight: 500;
  margin-bottom: 2px;
}

.info-item span {
  font-size: 1rem;
}

/* Footer */
.footer {
  background: var(--text);
  color: var(--white);
  padding: 48px 0 24px;
}

@media (max-width: 767px) {
  .footer {
    padding-bottom: 100px;
  }
}

.footer-content {
  display: flex;
  flex-direction: column;
  gap: 32px;
  margin-bottom: 32px;
}

@media (min-width: 768px) {
  .footer-content {
    flex-direction: row;
    justify-content: space-between;
    align-items: flex-start;
  }
}

.footer-brand .logo {
  color: var(--white);
  display: inline-block;
  margin-bottom: 12px;
}

.footer-brand p {
  font-size: 0.875rem;
  opacity: 0.7;
  max-width: 300px;
  line-height: 1.7;
}

.footer-links {
  display: flex;
  gap: 24px;
  flex-wrap: wrap;
}

.footer-links a {
  color: rgba(255,255,255,0.7);
  text-decoration: none;
  font-size: 0.875rem;
  transition: color 0.2s;
}

.footer-links a:hover {
  color: var(--white);
}

.footer-bottom {
  padding-top: 24px;
  border-top: 1px solid rgba(255,255,255,0.1);
}

.footer-bottom p {
  font-size: 0.8rem;
  opacity: 0.5;
  text-align: center;
}

/* WhatsApp Float */
.whatsapp-float {
  position: fixed;
  bottom: 80px;
  right: 20px;
  width: 56px;
  height: 56px;
  background: #25D366;
  color: var(--white);
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  box-shadow: 0 4px 20px rgba(37, 211, 102, 0.4);
  z-index: 999;
  transition: all 0.3s ease;
}

@media (min-width: 768px) {
  .whatsapp-float {
    bottom: 32px;
    right: 32px;
    width: 60px;
    height: 60px;
  }
}

.whatsapp-float:hover {
  transform: scale(1.1);
  box-shadow: 0 6px 30px rgba(37, 211, 102, 0.5);
}
